(function(){let e=$("#add-friend");e.on("click",(function(t){t.preventDefault(),$.ajax({type:"POST",url:$(e).attr("href"),success:function(t){!0===t.data.friend?(e.attr("class","friend"),e.html("Remove friend"),new Noty({theme:"relax",text:"Friend added!",type:"success",layout:"topRight",timeout:1e3}).show()):(e.attr("class","non-friend"),e.html("Add friend"),new Noty({theme:"relax",text:"Friend Removed!",type:"success",layout:"topRight",timeout:1e3}).show())},error:function(e){console.log("Err: ",e)}})}))})();